<script>
import { onMounted, onUnmounted, ref } from 'vue';
import gsap from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';
import AOS from 'aos';
import "aos/dist/aos.css";
import ServiceMo from "@/components/ServiceMo.vue";
import Portfolio from "@/components/Portfolio.vue";
gsap.registerPlugin(ScrollTrigger);

export default {
  name: 'AnimatedSections',
  setup() {
    onMounted(() => {
      gsap
          .timeline({
            scrollTrigger: {
              trigger: '.wrapper',
              start: 'top top',
              end: '+=150%',
              pin: true,
              scrub: true,
              markers: false, // 개발 중에 확인용, 배포 시 제거할 수 있음
            }
          })
          .to('img', {
            scale: 2,
            z: 350,
            transformOrigin: 'center center',
            ease: 'power1.inOut',
          })
          .to(
              '.section.hero',
              {
                scale: 1.0,
                transformOrigin: 'center center',
                ease: 'power1.inOut',
              },
              '<'
          );

      gsap.utils.toArray(".horizontal-container").forEach((container) => {
        let panels = gsap.utils.toArray(".panel", container);
        gsap
            .timeline({
              scrollTrigger: {
                trigger: container,
                start: "top top",
                end: "+=3000",
                pin: true,
                scrub: 2,
                markers: false,
              }
            })
            .to(panels, {
              xPercent: -100 * (panels.length - 1),
              ease: "none",
              duration: panels.length
            })
            .to(
                {},
                {
                  duration: 1 / panels.length
                }
            );
      });
    });

    const topFixed = ref(true); // 요소가 처음에는 fixed 상태
    const stickyElement = ref(null); // 고정될 요소
    const toptargetDiv = ref(null); // 기준이 되는 특정 div
    const marginBeforeRelease = 0.6; // 해제되기 전 미리 계산할 비율 (10%)

    const scrollHandler = () => {
      const targetRect = toptargetDiv.value.getBoundingClientRect();
      const stickyRect = stickyElement.value.getBoundingClientRect();

      const releasePoint = stickyRect.height + window.innerHeight * marginBeforeRelease;


      // 스크롤이 특정 div에 닿으면 fixed 해제
      if (targetRect.top <= releasePoint) {
        topFixed.value = false;
      } else {
        topFixed.value = true;
      }
    };

    onMounted(() => {
      window.addEventListener('scroll', scrollHandler);
    });

    onUnmounted(() => {
      window.removeEventListener('scroll', scrollHandler);
    });

    return {
      topFixed,
      stickyElement,
      toptargetDiv,
    };

  },
  components: {
    Portfolio
  },
  data() {
    return {
      isFixed: false, // fixed 레이아웃 여부

    };
  },
  methods: {
    observeTargetDiv() {
      const options = {
        root: null, // viewport를 기준으로 감지
        rootMargin: '0px 0px -3% 0px', // bottom에 -10%를 설정하여 타겟 div의 10% 이전부터 감지
        threshold: 0.3 // 타겟 div가 10% 보일 때 콜백 실행
      };

      const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          this.isFixed = entry.isIntersecting;
        });
      }, options);

      const targetDiv = this.$refs.targetDiv;
      observer.observe(targetDiv);
    }
  },
  created() {
    AOS.init();
  },
  beforeDestroy() {
    // 컴포넌트가 제거되기 전에 스크롤 이벤트 리스너 제거
    window.removeEventListener('scroll', this.handleScroll);
  },
  mounted() {
    AOS.refresh();
    this.observeTargetDiv();
  },
};
</script>

